$(window).load(function(){window.prettyPrint&&prettyPrint();$("li.dropdown > a.dropdown-toggle").each(function(e,t){$(this).append('<b class="caret"></b>')});$("ul.nav li.dropdown").hover(function(){$(this).find(".dropdown-menu").stop(!0,!0).delay(100).fadeIn()},function(){$(this).find(".dropdown-menu").stop(!0,!0).delay(100).fadeOut()});$(".submenu").scrollspy()});if(navigator.userAgent.match(/iPhone/i)||navigator.userAgent.match(/iPad/i)){var viewportmeta=document.querySelector('meta[name="viewport"]');if(viewportmeta){viewportmeta.content="width=device-width, minimum-scale=1.0, maximum-scale=1.0, initial-scale=1.0";document.body.addEventListener("gesturestart",function(){viewportmeta.content="width=device-width, minimum-scale=0.25, maximum-scale=1.6"},!1)}};